params.source:
vec3 pStart = vec3(0.0, start_y, start_sigma);
vec3 pEnd   = vec3(0.0, end_y, end_sigma);
vec2 qBase = pEnd.xy - pStart.xy;
float md = dot(qBase,qBase);
vec2 q = xy - pStart.xy;
float p = dot(q,qBase);
//float m = clamp((md - p) / md, 0.0, 1.0);
float m = smoothstep(md, 0.0, p);
m = pEnd.z + (pStart.z - pEnd.z) * m;
m = log2(m * 1.333333);
m = clamp(m, 0.0, 6.0);
output = m;

float scale;
if (mLo < 0.1) {
  cLo = mip0;
}

output = mix(cLo, cHi, m-mLo);

if (level == 0.0) {
  output = samplePass(mip0, xy, mip0_size / pow(2.0, level));
}
else if (level == 1.0) {
  output = samplePass(mip1, xy, mip0_size / pow(2.0, level));
}
else if (level == 2.0) {
  output = samplePass(mip2, xy, mip0_size / pow(2.0, level));
}
else if (level == 3.0) {
  output = samplePass(mip3, xy, mip0_size / pow(2.0, level));
}
else if (level == 4.0) {
  output = samplePass(mip4, xy, mip0_size / pow(2.0, level));
}
else if (level == 5.0) {
  output = samplePass(mip5, xy, mip0_size / pow(2.0, level));
}
else if (level == 6.0) {
  output = samplePass(mip6, xy, mip0_size / pow(2.0, level));
} else {
  return vec4(0.0);
}

params.helpers:
mat4x2 mvbUp(vec2 dc, float scl) {
  vec2 d = dc * scl - 0.5;
  vec2 c = floor(d);
  vec2 x = c - d + 1.0;
  vec2 X = d - c;
  vec2 x3 = x * x * x;
  vec2 coeff = 0.5 * x * x + 0.5 * x + 0.166667;
  vec2 w1 = -0.333333 * x3 + coeff;
  vec2 w2 = 1.0 - w1;
  vec2 o1 = (-0.5 * x3 + coeff) / w1 + c - 0.5;
  vec2 o2 = (X * X * X / 6.0) / w2 + c + 1.5;
  return mat4x2(w1, w2, o1, o2);
}

vec4 applyWeights(mat4x2 w, vec4 s00, vec4 s10, vec4 s01, vec4 s11) {
  return w[0].x * w[0].y * s00 + w[1].x * w[0].y * s10 + w[0].x * w[1].y * s01 + w[1].x * w[1].y * s11;
}

