c_edge.rgb = mix(vec3(1.0), c_edge.rgb, lumin_edge);

c_edge.a *= e * f * 0.05;

c_edge.a += (mix(0.08, 0.22, lumin_edge) + l * f * 0.6 + selection * f * 0.2) * e;

c_edge.rgb = mix(c_edge.rgb, vec3(1.0), l * f + selection * f);

c_edge.rgb *= c_edge.a;

output = c_edge;

n = normalize(n);
float r = max(0.0, dot(n.xyz, i.xyz)) + max(0.0, dot(n.xyz, vec3(-i.xy, i.z)));

output = r * 0.7;

vec4 r = c_ui * mix(1.0, t, show_thumb);
r = mix(r, vec4(1.0), thumb * show_thumb);
output = r;

float r = max(sdf+22.0, 0.0);
r /= 42.0;
output = float(exp(-r*r)) * show_thumb;

// Blend the source and destination colors
vec3 blendedColor = src.rgb + dst.rgb * (1.0 - src.a);

// Calculate the alpha of the resulting color
float blendedAlpha = src.a + dst.a * (1.0 - src.a);

// Return the blended color with the blended alpha
return vec4(blendedColor, blendedAlpha);
