set -o pipefail; echo "PWD=$PWD"; echo "commits (source files only):"; git log --since='2026-01-06 00:00' --pretty=format:'%H' -- ':(glob)**/*.rs' ':(glob)**/*.wgsl' ':(glob)**/*.js' ':(glob)**/*.ts' 2>&1 | wc -l; echo "line-stats (rs/wgsl/js/ts):"; git log --since='2026-01-06 00:00' --numstat --pretty=format: -- ':(glob)**/*.rs' ':(glob)**/*.wgsl' ':(glob)**/*.js' ':(glob)**/*.ts' 2>&1 | awk 'BEGIN{add=0;del=0;files=0} NF==3 {if($1!="-"){add+=$1} if($2!="-"){del+=$2} files+=1} END{printf("added\t%d\ndeleted\t%d\nnet\t%d\nfiles\t%d\n", add, del, add-del, files)}'